import React,{useEffect,useState} from "react";
import './style.css';

function App() {


  const[nutri, setnutri] = useState([]);
  useEffect(()=>{

    function loadAPI(){

      let url = 'https://sujeitoprogramador.com/rn-api/?api=posts'
      fetch(url)

      .then((r)=>r.json())
      .then((json)=>{


        setnutri(json);
      })


    }
    loadAPI()
  },[]);

  return (



    <div className="container">


  <header>
    <strong>React Nutri</strong>
  </header>

    {nutri.map((item)=>{
      return(
        <article key={item.id} className="post">
          <strong className="titulo" key={item.titulo}>

          </strong>

        </article>
      )
    })}

    </div>
  );
}

export default App;
